useEffect(() => {
     if (!resultado || !resultado.nombre.substring().toLowerCase().includes(letter)) {
         setResultado({ nombre: "No se ha encontrado ningún rey" }); } },
         [resultado, letter]);

    return (
    <div>
     {resultado.nombre} 
    </div>
  );
}

export default Tarjeta
